const app = express()
const http = require('http').Server(app)
const port = 3007
var io = require('socket.io')(http, {
  allowEIO3: true,
  cors: {
    origin: 'http://localhost:8080',
    // origin: 'http://192.168.0.198:8080',
    methods: ['GET', 'POST'],
    allowedHeaders: ['*'],
    credentials: true
  }
})

import { obj2, obj3, add } from './test2' // ES6 import

